fn transmute_examples() {
    // 危险但有用的类型转换
    unsafe {
        // 将i32转换为[u8; 4]
        let num: i32 = 42;
        let bytes: [u8; 4] = mem::transmute(num);

        // 将裸指针转换为usize
        let ptr: *const i32 = &42;
        let addr: usize = mem::transmute(ptr);

        // 将生命周期标记去除（极其危险！）
        let string = "hello";
        let static_str: &'static str = mem::transmute(string);
    }
}
